import React from 'react';

import useSticky from './../../component/hooks/useSticky.js';
import Navbar from './../../component/Navbar/Navbar';
import 'swiper/swiper.scss';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';
import 'swiper/components/scrollbar/scrollbar.scss';
import { Col, Row, Rate } from 'antd';
import Footer from './../../component/Footer/Footer';

const About = () => {
  const { isSticky, element } = useSticky();
  return (
    <div>
      <Navbar sticky={isSticky} />
      <main style={{ backgroundColor: '#FAFAFA' }}>
        <section className="welcome">
          <div ref={element}>
            <div
              style={{
                display: 'flex',
                flexDirection: 'column',
                margin: '100px 150px',
                alignItems: 'center',
              }}
            >
              <p
                style={{
                  fontWeight: '700',
                  fontSize: '36px',
                  marginBottom: '25px',
                }}
              >
                Welcome To
              </p>
              <img
                src="./image/logo/logo.png"
                style={{
                  width: '480px',
                  height: '100px',
                  marginBottom: '33px',
                }}
                alt="logo"
              />
              <p
                style={{
                  fontWeight: '400',
                  fontFamily: 'Lato',
                  fontSize: '24px',
                }}
              >
                Be like Aphrodite
              </p>
              <div
                style={{
                  display: 'flex',
                  flexDirection: 'row',
                  alignItems: 'center',
                }}
              >
                <img
                  src="./image/about/about1.png"
                  alt="about 1"
                  style={{ width: '1030px', height: '850px' }}
                />
                <div
                  style={{
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'flex-start',
                  }}
                >
                  <p style={{ fontWeight: '700', fontSize: '36px' }}>
                    Our Inspiration
                  </p>
                  <p style={{ fontFamily: 'Lato' }}>
                    Humanity have always been amazed with the glorious beauty of
                    a legendary goddess - Aphrodite. For both ancient artists
                    and present creators, the goddess has been admired as the
                    greatest inspiration for many masterpieces.
                  </p>

                  <p style={{ fontFamily: 'Lato' }}>
                    APHRO is also formed on this inspiration. We aim to help you
                    nurture the classic beauty with the exclusive fomula
                    skin-care routine.
                  </p>
                </div>
              </div>
              <div
                style={{
                  display: 'flex',
                  flexDirection: 'row',
                  alignItems: 'center',
                }}
              >
                <div style={{ display: 'flex', flexDirection: 'column',marginLeft:'100px' }}>
                  <p style={{ fontSize: '36px', fontWeight: '700' }}>
                    Our Products
                  </p>
                  <p style={{ fontWeight: '400', fontFamily: 'Lato' }}>
                    Each one of our products are easy-to-use hybrids that make
                    your skin look naturally glowing and lit-from-within.
                  </p>
                  <p style={{ fontWeight: '400', fontFamily: 'Lato' }}>
                    We only formulate with the highest quality natural
                    ingredients, and believe what’s not in the formula is just
                    as important as what is.
                  </p>
                  <p style={{ fontWeight: '400', fontFamily: 'Lato' }}>
                    Our skincare essentials are clean, cruelty-free and free of
                    parabens, sulfates, and phthalates, among others
                  </p>
                </div>
                <div style={{position:'absolute'}}>
                    <img />
                </div>
              </div>
            </div>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default About;
