import React, { useState } from 'react';
import './Navbar.css';
import { NavLink } from 'react-router-dom';
import { Modal } from 'antd';
import { modalData } from './data';
const Navbar = ({ sticky }) => {
  const token = localStorage.getItem('tokenWeb');
  const [isModalVisible, setIsModalVisible] = useState(false);
  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <div>
      <nav
        className={
          sticky
            ? 'navbar navbar-sticky navbar-expand-lg'
            : 'navbar navbar-expand-lg'
        }
        style={{ padding: '17px 50px' }}
      >
        {/* <a className="navbar-brand" href="/"> */}
        <NavLink to="/">
          <img
            src="./image/logo/logo.png"
            style={{ width: '155px', height: '32px' }}
            alt="logo"
          />
        </NavLink>

        {/* </a> */}
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon" />
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
              <NavLink to="/store" style={{ color: 'black' }}>
                Shop
              </NavLink>
            </li>
            <li className="nav-item active">
              <NavLink to="/kits" style={{ color: 'black' }}>
                Kits
              </NavLink>
            </li>

            <li className="nav-item active">
              <NavLink to="/about" style={{ color: 'black' }}>
                About
              </NavLink>
            </li>
            <li className="nav-item active">
              <NavLink to="/contact" style={{ color: 'black' }}>
                Contact
              </NavLink>
            </li>
          </ul>
          <ul className="navbar-nav">
            <li className="nav-item active menu-left">
              <NavLink to={token ? '/profile' : '/login'}>
                <img
                  src={
                    token
                      ? './image/Product/productAvt.png'
                      : './image/Home/blackPerson.png'
                  }
                  alt="aphro"
                  style={{ width: '23px', height: '23px' }}
                />
              </NavLink>
            </li>
            <li
              className="nav-item active menu-left"
              onClick={() => setIsModalVisible(true)}
            >
              <img
                src="./image/Home/blackHeart.png"
                alt="vector"
                style={{ width: '23px', height: '23px' }}
              />
            </li>
            <li className="nav-item active">
              <img
                src="./image/Home/blackBag.png"
                alt="vector 2"
                style={{ width: '23px', height: '23px' }}
              />
            </li>
          </ul>
        </div>
      </nav>
      <Modal
        title="Shopping Cart"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
        cancelButtonProps={{ style: { display: 'none' } }}
        okButtonProps={{ style: { display: 'none' } }}
      >
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
          }}
        >

          <div style={{padding: '15px 35px'}}>
            {modalData.map((value, index) => {
              return (
                <div
                  style={{
                    display: 'flex',
                    flexDirection: 'row',
                    alignItems: 'flex-end',
                    marginBottom: index !== modalData.length -1 && '35px',
                  }}
                  key={index}
                >
                  <div style={{ position: 'relative' }}>
                    <img
                      src={value.image}
                      style={{
                        width: '140px',
                        height: '170px',
                        marginRight: '30px',
                      }}
                      alt="image product"
                    />
                    <div
                      style={{
                        width: '18px',
                        height: '18px',
                        borderRadius: '18px',
                        backgroundColor: 'grey',
                        position: 'absolute',
                        right: '20px',
                        top: '-8px',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                      }}
                    >
                      <span style={{ color: '#cecece' }}>x</span>
                    </div>
                  </div>

                  <div style={{ display: 'flex', flexDirection: 'column' }}>
                    <p
                      style={{
                        fontWeight: '700',
                        fontFamily: 'Lato',
                        fontSize: '14px',
                      }}
                    >
                      {value.title}
                    </p>
                    <p
                      style={{
                        fontFamily: 'Lato',
                        fontWeight: '300',
                        fontSize: '14px',
                      }}
                    >
                      50ml
                    </p>
                    <p
                      style={{
                        fontFamily: 'Lato',
                        fontWeight: '300',
                        fontSize: '14px',
                      }}
                    >
                      $ 34
                    </p>
                    <div
                      style={{
                        width: '138px',
                        height: '42px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                        border: 'solid 1px black',
                        paddingLeft: '10px',
                        paddingRight: '10px',
                      }}
                    >
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        -
                      </span>
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        1
                      </span>
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        +
                      </span>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
          <div style={{display:'flex',flexDirection:'row',padding: '21px 30px 50px 30px'}}>
              <div style={{display:'flex',flexDirection:'row'}}>

              </div>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default Navbar;
