import React from 'react';
import SwiperCore, {
  Navigation,
  Pagination,
  Scrollbar,
  A11y,
  Autoplay,
} from 'swiper';

import { Swiper, SwiperSlide } from 'swiper/react';
import useSticky from './../../component/hooks/useSticky.js';
import { backGroundKits } from './data';
import Navbar from './../../component/Navbar/Navbar';
import 'swiper/swiper.scss';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';
import 'swiper/components/scrollbar/scrollbar.scss';
import { Col, Row, Rate, Menu, Dropdown } from 'antd';
import Footer from './../../component/Footer/Footer';
import { DownOutlined } from '@ant-design/icons';

SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);

const Kits = () => {
  const { isSticky, element } = useSticky();
  const menu = (
    <Menu>
      <Menu.Item key="0">
        <span style={{}}>Categories</span>
      </Menu.Item>
      <Menu.Item key="1">
        <span>Categories 2</span>
      </Menu.Item>
    </Menu>
  );

  SwiperCore.use([Autoplay]);
  return (
    <div>
      <Navbar sticky={isSticky} />
      <main style={{ backgroundColor: '#FAFAFA' }}>
        <section className="welcome">
          <div ref={element}>
            <Swiper autoplay={{ delay: 5000 }}>
              {backGroundKits.map((value, index) => {
                return (
                  <SwiperSlide key={index}>
                    <div
                      style={{
                        height: '570px',
                        backgroundImage: `url(${value.image})`,
                        width: '100%',
                        backgroundRepeat: 'no-repeat',
                        backgroundPosition: 'center',
                        backgroundSize: 'cover',
                      }}
                    ></div>
                  </SwiperSlide>
                );
              })}
            </Swiper>
          </div>
        </section>
        <section
          className="kits"
          style={{ width: '100%', padding: '30px 70px' }}
        >
          <div style={{ margin: '0 50px' }}>
            <Dropdown overlay={menu} trigger={['click']}>
              <div
                style={{
                  display: 'flex',
                  flexDirection: 'row',
                  alignItems: 'center',
                  width: '100px',
                }}
              >
                <span
                  style={{
                    fontWeight: 'bold',
                    //   fontFamily: 'Lato',
                    fontSize: '14px',
                  }}
                >
                  Categories
                </span>
                <DownOutlined
                  style={{
                    color: '#FF9592',
                    fontSize: '14px',
                    marginLeft: '5px',
                  }}
                />
              </div>
            </Dropdown>
          </div>
          <div style={{margin: '47px',display: 'flex',flexDirection: 'column',alignItems: 'center'}}>
                <span style={{fontWeight:'bold',fontSize:'36px'}}>Core Series</span>
                <span style={{marginTop:'50px',marginBottom:'50px',fontWeight:'400',fontSize:'48px'}}>The Pearly Goddess</span>
                <span style={{width:'674px',wordWrap:'break-word',fontWeight:'300',fontSize:'18px'}}></span>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default Kits;
