import React from 'react';
import SwiperCore, {
  Navigation,
  Pagination,
  Scrollbar,
  A11y,
  Autoplay,
} from 'swiper';

import useSticky from './../../component/hooks/useSticky.js';
import Navbar from './../../component/Navbar/Navbar';

import { Col, Row, Rate, Input } from 'antd';
// import Footer from "./../../component/Footer/Footer"
import Footer from './../../component/Footer/Footer';
import { UserOutlined,DownOutlined } from '@ant-design/icons';

const ShippingInfo = () => {
  const { isSticky, element } = useSticky();
  SwiperCore.use([Autoplay]);
  const suffix = (
    <UserOutlined
      style={{
        fontSize: 16,
        cp: 'black',
      }}
    />
  );
  const suffix2 = (
    <DownOutlined
      style={{
        fontSize: 16,
        color: '#FF9592',
        cp: '#FF9592',
      }}
    />
  );
  return (
    <div>
      <Navbar sticky={isSticky} />
      <main style={{ backgroundColor: '#FAFAFA' }}>
        <section className="welcome">
          <div ref={element} style={{ padding: ' 0 70px 70px 70px' }}>
            <Row>
              <Col
                xl={12}
                lg={12}
                md={12}
                sm={24}
                xs={24}
                style={{ paddingRight: '70px', marginTop: '120px' }}
              >
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <img
                    src="./image/logo/logo.png"
                    alt="logo"
                    style={{ width: '155px', height: '32px' }}
                  />
                  <p
                    style={{
                      fontFamily: 'Lato',
                      marginTop: '25px',
                      marginBottom: '50px',
                    }}
                  >
                    <span style={{ color: '#FF9592' }}>Cart</span> {'>'}{' '}
                    Information {'>'} Shipping/Payment
                  </p>

                  <div
                    style={{
                      position: 'relative',
                      padding: '21px 40px',
                      border: 'solid 1px #cecece',
                      width: '100%',
                    }}
                  >
                    <div
                      style={{
                        padding: '0 10px',
                        position: 'absolute',
                        top: '-12px',
                        left: '40%',
                        zIndex: 1000,
                        backgroundColor: 'white',
                      }}
                    >
                      <span>Express checkout</span>
                    </div>
                    <div
                      style={{
                        width: '100$',
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                      }}
                    >
                      <img
                        src="./image/shipping/zalo.png"
                        alt="zalo"
                        style={{ width: '80px', height: '47px' }}
                      />
                      <img
                        src="./image/shipping/momo.png"
                        alt="momo"
                        style={{ width: '53px', height: '35px' }}
                      />
                      <img
                        src="./image/shipping/paypal.png"
                        alt="paypal"
                        style={{ width: '85px', height: '47px' }}
                      />
                      <img
                        src="./image/shipping/air.png"
                        alt="air"
                        style={{ width: '86px', height: '29px' }}
                      />
                    </div>
                  </div>
                  <div
                    style={{
                      marginTop: '25px',
                      width: '100%',
                      textAlign: 'center',
                    }}
                  >
                    <span>OR</span>
                  </div>
                  <div
                    style={{
                      display: 'flex',
                      flexDirection: 'row',
                      marginTop: '25px',
                      paddingBottom: '35px',
                      borderBottom: 'solid 1px #cecece',
                      marginBottom: '15px',
                    }}
                  >
                    <div className="button-shipping">
                      <span
                        style={{
                          fontWeight: '700',
                          fontSize: '14px',
                          fontFamily: 'Lato',
                        }}
                      >
                        LOGIN
                      </span>
                      <span
                        style={{
                          fontWeight: '400',
                          fontSize: '14px',
