import React from 'react';


import useSticky from './../../component/hooks/useSticky.js';
import Navbar from './../../component/Navbar/Navbar';
import {dataShip} from './data'
import { Col, Row, Rate,Input } from 'antd';
// import Footer from "./../../component/Footer/Footer"
import Footer from './../../component/Footer/Footer'

const OrderStatus = () =>{
    const { isSticky, element } = useSticky();
    return(
        <div>
        <Navbar sticky={isSticky} />
        <main style={{ backgroundColor: '#FAFAFA' }}>
          <section className="welcome">
            <div ref={element}>
                <div style={{padding: '93px 26%',display:'flex',flexDirection:'column',justifyContent:'center',alignItems:'center'}}>
                    <img src="./image/shipping/tickShip.png" alt="tick ship" style={{width: '150px',height:'150px'}} />
                    <p style={{marginTop:'30px',marginBottom:'30px',fontSize:'36px',fontWeight:'700'}}>Thank you for your purchase</p>
                    <p style={{fontFamily:'Lato',fontWeight:'400'}}>Order #2605</p>
                    <div style={{marginTop:'50px',padding: '20px',backgroundColor:'#fff',border: 'solid 1px #cecece'}}>
                        <p style={{fontFamily:'Lato',fontWeight:'700'}}>Hi Veri Beautiful Lady, your order is confirmed.</p>
                        <p style={{fontFamily:'Lato',fontWeight:'300',marginBottom:'0'}}> We’ve accepted your order and we’re getting it ready. Comback to this page for updates on your order status.</p>
                    </div>



                    <div style={{ borderBottom: 'solid 1px #cecece',width: '100%',backgroundColor:'#fff',marginTop:'30px' }}>
                  {dataShip.map((value, index) => {
                    return (
                      <div
                        style={{
                          display: 'flex',
                          flexDirection: 'row',
                          alignItems: 'center',
                          marginBottom: '30px',
                        }}
                        key={index}
                      >
                        <div style={{ position: 'relative' }}>
                          <img
                            src={value.image}
                            style={{
                              width: '80px',
                              height: '80px',
                              marginRight: '30px',
                            }}
                            alt="product"
                          />
                          <div
                            style={{
                              width: '18px',
                              height: '18px',
                              borderRadius: '18px',
                              backgroundColor: 'grey',
                              position: 'absolute',
                              right: '20px',
                              top: '-8px',
                              display: 'flex',
                              justifyContent: 'center',
                              alignItems: 'center',
                            }}
                          >
                            <span style={{ color: '#cecece' }}>1</span>
                          </div>
                        </div>
                        <div
                          style={{
                            display: 'flex',
                            flexDirection: 'row',
                            justifyContent: 'space-between',
                            alignItems: 'center',
                            width: '100%',
                          }}
                        >
                          <div
                            style={{
                              display: 'flex',
                              flexDirection: 'column',
                            }}
                          >
                            <p
                              style={{
                                fontWeight: '700',
                                fontFamily: 'Lato',
                                fontSize: '14px',
                              }}
                            >
                              {value.title}
                            </p>
                            <p
                              style={{
                                fontFamily: 'Lato',
                                fontWeight: '300',
                                fontSize: '14px',
                              }}
                            >
                              50ml
                            </p>
                          </div>
                          <p
                            style={{
                              fontFamily: 'Lato',
                              fontWeight: '700',
                              fontSize: '14px',
                            }}
                          >
                            $ 34
                          </p>
                        </div>
                      </div>
                    );
                  })}
                </div>
                <div
                  style={{
                    display: 'flex',
                    flexDirection: 'row',
                    marginTop: '21px',
                    paddingBottom: '22px',
                    borderBottom: 'solid 1px #cecece',
                  }}
                >
                  <Input
                    size="large"
                    style={{
                      fontFamily: 'Lato',
                      fontWeight: 'bold',
                      fontSize: '14px',
                      width: '100%',
                      marginRight: '18px',
                    }}
                    placeholder="Gift card or discount code"
                  />
                  <div
                    className="button-main"
                    style={{
                      width: '142px',
                      height: '45px',
                    }}
                  >
                    <span
                      style={{
                        fontWeight: '700',
                        fontSize: '14px',
                      }}
                    >
                      Apply
                    </span>
                  </div>
                </div>
                <div
                  style={{
                    marginTop: '25px',
                    display: 'flex',
                    flexDirection: 'column',
                    paddingBottom: '25px',
                    borderBottom: 'solid 1px #cecece',
                  }}
                >
                  <div
                    style={{
                      display: 'flex',
                      flexDirection: 'row',
                      justifyContent: 'space-between',
                      alignItems: 'center',
                    }}
                  >
                    <p style={{ fontWeight: '400', fontFamily: 'Lato' }}>
                      Subtotal
                    </p>
                    <p style={{ fontWeight: '700', fontFamily: 'Lato' }}>
                      $ 102
                    </p>
                  </div>
                  <div
                    style={{
                      display: 'flex',
                      flexDirection: 'row',
                      justifyContent: 'space-between',
                      alignItems: 'center',
                      marginTop: '20px',
                    }}
                  >
                    <p style={{ fontWeight: '400', fontFamily: 'Lato' }}>
                      Shipping
                    </p>
                    <p style={{ fontWeight: '300', fontFamily: 'Lato' }}>
                      calculate at next step
                    </p>
                  </div>
                </div>
                <div
                  style={{
                    display: 'flex',
                    flexDirection: 'row',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    marginTop: '25px',
                  }}
                >
                  <p style={{ fontWeight: '400', fontFamily: 'Lato' }}>Total</p>
                  <p style={{ fontWeight: '700', fontFamily: 'Lato' }}>$ 102</p>
                </div>
                </div>
            </div>
          </section>
        </main>
        <Footer />
      </div>
    )
}

export default OrderStatus
