import React from 'react';
import useSticky from './../../component/hooks/useSticky.js';
import Navbar from './../../component/Navbar/Navbar';
import { Col, Row, Rate } from 'antd';
import Footer from './../../component/Footer/Footer';

const ProductDetail = () => {
  const { isSticky, element } = useSticky();

  return (
    <div>
      <Navbar sticky={isSticky} />
      <div style={{ backgroundColor: '#FAFAFA' }}>
        <div className="welcome">
          <div ref={element} className="paddingScreen">
            <Row>
              <Col
                xl={12}
                lg={12}
                md={12}
                sm={24}
                xs={24}
                style={{ padding: '0 50px' }}
              >
                <div
                  style={{
                    padding: '15px',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                  }}
                >
                  <img src="./image/Product/product1.png" alt="product 1" />
                  <div
                    style={{
                      display: 'flex',
                      flexDirection: 'row',
                      justifyContent: 'flex-start',
                      marginTop: '20px',
                    }}
                  >
                    <img
                      src="./image/Product/product2.png"
                      alt="product 2"
                      style={{ width: '61px', height: '61px' }}
                    />
                    <img
                      src="./image/Product/product2.png"
                      alt="product 2"
                      style={{
                        width: '61px',
                        height: '61px',
                        marginLeft: '20px',
                      }}
                    />
                  </div>
                  <div
                    style={{
                      width: '100%',
                      borderTop: 'solid 1px black',
                      borderBottom: 'solid 1px black',
                      marginTop: '20px',
                    }}
                  >
                    <div
                      style={{
                        padding: '15px',
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                        borderBottom: 'solid 1px black',
                      }}
                    >
                      <span style={{ fontWeight: 'bold', fontSize: '14px' }}>
                        How To Use{' '}
                      </span>
                      <span style={{ fontWeight: 'bold', fontSize: '14px' }}>
                        +
                      </span>
                    </div>

                    <div
                      style={{
                        padding: '15px',
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                      }}
                    >
                      <span style={{ fontWeight: 'bold', fontSize: '14px' }}>
                        Ingredients{' '}
                      </span>
                      <span style={{ fontWeight: 'bold', fontSize: '14px' }}>
                        +
                      </span>
                    </div>
                  </div>
                </div>
              </Col>
              <Col
                xl={12}
                lg={12}
                md={12}
                sm={24}
                xs={24}
                style={{ padding: '0 50px' }}
              >
                <div
                  style={{
                    padding: '15px',
                    display: 'flex',
                    flexDirection: 'column',
                  }}
                >
                  <p style={{ fontWeight: '700', fontSize: '24px' }}>
                    Pearly Goddess Gentle Make-up Remover
                  </p>
                  <p
                    style={{
                      fontWeight: 'bold',
                      fontSize: '18px',
                      fontFamily: 'Lato',
                    }}
                  >
                    APHRO’s Original Make-up Remover
                  </p>
                  <div
                    style={{
                      display: 'flex',
                      flexDirection: 'row',
                      alignItems: 'center',
                    }}
                  >
                    <Rate
                      disabled
                      defaultValue={4}
                      style={{ color: '#FF9592' }}
                    />
                    <span
                      style={{
                        fontWeight: 'bold',
                        fontFamily: 'Lato',
                        fontSize: '14px',
                        marginLeft: '20px',
                        marginTop: '5px',
                      }}
                    >
                      998 Reviews
                    </span>
                  </div>
                  <p
                    style={{
                      fontWeight: '700',
                      fontSize: '24px',
                      marginTop: '30px',
                    }}
                  >
                    $9.80
                  </p>
                  <div
                    style={{
                      display: 'flex',
                      flexDirection: 'row',
                      justifyContent: 'space-between',
                      alignItems: 'center',
                      paddingRight: '100px',
                    }}
                  >
                    <div
                      style={{
                        width: '142px',
                        height: '50px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        border: 'solid 1px black',
                      }}
                    >
                      <span style={{ fontWeight: '400', fontSize: '14px' }}>
                        SIZE 1 oz/ 30 mL
                      </span>
                    </div>
                    <div
                      style={{
                        width: '195px',
                        height: '50px',
                        backgroundColor: '#FF9592',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                      }}
                    >
                      <span
                        style={{
                          fontWeight: '700',
                          fontSize: '14px',
                          color: '#000',
                        }}
                      >
                        Add to card
                      </span>
                    </div>
                  </div>

                  <div
                    style={{
                      marginTop: '21px',
                      display: 'flex',
                      flexDirection: 'row',
                      justifyContent: 'space-between',
                      alignItems: 'center',
                      paddingRight: '100px',
                    }}
                  >
                    <div
                      style={{
                        width: '142px',
                        height: '50px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                        border: 'solid 1px black',
                        paddingLeft: '10px',
                        paddingRight: '10px',
                      }}
                    >
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        -
                      </span>
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        1
                      </span>
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        +
                      </span>
                    </div>
                    <div
                      style={{
                        width: '195px',
                        height: '50px',
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'center',
                        alignItems: 'center',
                        cursor: 'pointer',
                      }}
                    >
                      <img src="./image/Home/heart-1.png" alt="heart circle" />
                      <span
                        style={{
                          fontWeight: '700',
                          fontSize: '14px',
                          color: '#000',
                          marginLeft: '7px',
                        }}
                      >
                        Add to wishlist
                      </span>
                    </div>
                  </div>

                  <div
                    style={{
                      display: 'flex',
                      flexDirection: 'column',
                      marginTop: '15px',
                    }}
                  >
                    <p
                      style={{
                        fontWeight: 'bold',
                        fontSize: '14px',
                      }}
                    >
                      Which skin type is it good for?
                    </p>
                    <p style={{ fontWeight: '400' }}>
                      <span style={{ fontWeight: '700' }}>✔</span> Normal
                    </p>
                    <p style={{ fontWeight: '400' }}>
                      <span style={{ fontWeight: '700' }}>✔</span> Oily
                    </p>
                    <p style={{ fontWeight: '400' }}>
                      <span style={{ fontWeight: '700' }}>✔</span> Combination
                    </p>
                    <p style={{ fontWeight: '400' }}>
                      <span style={{ fontWeight: '700' }}>✔</span> Dry
                    </p>
                    <p style={{ fontWeight: '400' }}>
                      <span style={{ fontWeight: '700' }}>✔</span> Sensi
                    </p>
                    <p style={{marginTop:''}}>

                    </p>
                  </div>
                </div>
              </Col>
            </Row>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ProductDetail;
