import React from 'react';
import useSticky from './../../component/hooks/useSticky.js';
import Navbar from './../../component/Navbar/Navbar';
import { Col, Row, Rate } from 'antd';
import Footer from './../../component/Footer/Footer';

const ProductDetail = () => {
  const { isSticky, element } = useSticky();
  return (
    <div>
      <Navbar sticky={isSticky} />
      <main style={{ backgroundColor: '#FAFAFA' }}>
        <section className="welcome">
          <div ref={element} style={{ padding: '172px 120px' }}>
            <Row>
              <Col xl={12} lg={12} md={12} sm={24} xs={24}>
                <div
                  style={{
                    padding: '15px',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                  }}
                >
                  <img src="./image/Product/product1.png" alt="product 1" />
                  <div
                    style={{
                      display: 'flex',
                      flexDirection: 'row',
                      justifyContent: 'flex-start',
                      marginTop: '20px',
                    }}
                  >
                    <img
                      src="./image/Product/product2.png"
                      alt="product 2"
                      style={{ width: '61px', height: '61px' }}
                    />
                    <img
                      src="./image/Product/product2.png"
                      alt="product 2"
                      style={{
                        width: '61px',
                        height: '61px',
                        marginLeft: '20px',
                      }}
                    />
                  </div>
                  <div
                    style={{
                      width: '100%',
                      borderTop: 'solid 1px black',
                      borderBottom: 'solid 1px black',
                      marginTop: '20px',
                    }}
                  >
                    <div
                      style={{
                        padding: '15px',
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                        borderBottom: 'solid 1px black',
                      }}
                    >
                      <span style={{ fontWeight: 'bold', fontSize: '14px' }}>
                        How To Use{' '}
                      </span>
                      <span style={{ fontWeight: 'bold', fontSize: '14px' }}>
                        +
                      </span>
                    </div>

                    <div
                      style={{
                        padding: '15px',
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                      }}
                    >
                      <span style={{ fontWeight: 'bold', fontSize: '14px' }}>
                        Ingredients{' '}
                      </span>
                      <span style={{ fontWeight: 'bold', fontSize: '14px' }}>
                        +
                      </span>
                    </div>
                  </div>
                </div>
              </Col>
              <Col xl={12} lg={12} md={12} sm={24} xs={24}>
                <div
                  style={{
                    padding: '15px',
                    display: 'flex',
                    flexDirection: 'column',
                  }}
                >
                  <p style={{ fontWeight: '700', fontSize: '24px' }}>
                    Pearly Goddess Gentle Make-up Remover
                  </p>
                  <p
                    style={{
                      fontWeight: 'bold',
                      fontSize: '18px',
                      fontFamily: 'Lato',
                    }}
                  >
                    APHRO’s Original Make-up Remover
                  </p>
                  <div
                    style={{
                      display: 'flex',
                      flexDirection: 'row',
                      alignItems: 'center',
                    }}
                  >
                    <Rate
                      disabled
                      defaultValue={4}
                      style={{ color: '#FF9592' }}
                    />
                    <span
                      style={{
                        fontWeight: 'bold',
                        fontFamily: 'Lato',
                        fontSize: '14px',
                        marginLeft: '20px',
                        marginTop: '5px',
                      }}
                    >
                      998 Reviews
                    </span>
                  </div>
                  <p style={{fontWeight:'700',fontSize:'24px',marginTop:'30px'}}>$9.80</p>
                </div>
              </Col>
            </Row>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default ProductDetail;
