import React,{useState} from 'react';
import SwiperCore, {
  Navigation,
  Pagination,
  Scrollbar,
  A11y,
  Autoplay,
} from 'swiper';

import { Swiper, SwiperSlide } from 'swiper/react';
import useSticky from './../../component/hooks/useSticky.js';
import { backGroundMain, aphorodite } from './data';
import Navbar from './../../component/Navbar/Navbar';
import 'swiper/swiper.scss';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';
import 'swiper/components/scrollbar/scrollbar.scss';
import { Col, Row, Rate } from 'antd';
// import Footer from "./../../component/Footer/Footer"
import Footer from './../../component/Footer/Footer'
import { NavLink } from 'react-router-dom';

SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);

const Home = () => {
  const blackHeart = "./image/Home/blackHeart.png"
  const roundHeart = "./image/Home/heart-1.png"
  const { isSticky, element } = useSticky();
  const [heartUrl,setHeartUrl] = useState(-1)
  SwiperCore.use([Autoplay]);
  return (
    <div>
      <Navbar sticky={isSticky} />
      <main style={{ backgroundColor: '#FAFAFA' }}>
        <section className="welcome">
          <div ref={element}>
            <Swiper autoplay={{ delay: 5000 }}>
              {backGroundMain.map((value, index) => {
                return (
                  <SwiperSlide key={index} pagination={{ clickable: true}}>
                    <div
                      style={{
                        height: '570px',
                        backgroundImage: `url(${value.image})`,
                        width: '100%',
                        backgroundRepeat: 'no-repeat',
                        backgroundPosition: 'center',
                        backgroundSize: 'cover',
                      }}
                    ></div>
                  </SwiperSlide>
                );
              })}
            </Swiper>
          </div>
        </section>
        <section className="home" style={{ width: '100%' }}>
          <div
            style={{
              marginTop: '150px',
              width: '100%',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <span
              style={{
                fontWeight: 'bold',
                fontSize: '36px',
                marginBottom: '10px',
                fontFamily:'Playfair Display'
              }}
            >
              Be Aphrodite Everyday
            </span>
            <span
              style={{
                fontWeight: '300',
                fontSize: '18px',
                fontFamily: 'lato',
              }}
            >
              Show the world the true beauty of a true goddess{' '}
            </span>
          </div>
          <div style={{ margin: '70px' }}>
            <Row>
              {aphorodite.map((value, index) => {
                return (
                  <Col xl={6} key={index} lg={12} md={12} sm={12} xs={24}>
                    <NavLink
                    to="/product-detail"
                      style={{
                        width: '100%',
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                        color: '#000'
                      }}
                    >
                      <div
                        style={{
                          width: '294px',
                          height: '382px',
                          backgroundImage: `url(${value.image})`,
                          backgroundRepeat: 'no-repeat',
                          backgroundPosition: 'center',
                          backgroundSize: 'cover',
                        }}
                        onMouseEnter={() => setHeartUrl(index)}
                        onMouseLeave={() => setHeartUrl(-1)}

                      >
                        <div
                          style={{
                            display: 'flex',
                            flexDirection: 'row',
                            padding: '10px 10px',
                            justifyContent: 'space-between',
                          }}
                        >
                          <span
                            style={{
                              backgroundColor: '#fff',
                              fontSize: '14px',
                              fontWeight: '400',
                              color: 'black',
                              padding: '1px 2px',
                            }}
                          >
                            Bestseller
                          </span>
                          <img
                            alt="Vector"
                            src={heartUrl === index ? blackHeart : roundHeart}
                            style={{ width: '23px', height: '21px' }}
                          />
                        </div>
                      </div>
                      <span
                        style={{
                          fontWeight: '700',
                          fontSize: '14px',
                          marginTop: '10px',
                          fontFamily:'Playfair Display'
                        }}
                      >
                        {value.title}
                      </span>
                      <Rate
                        disabled
                        defaultValue={5}
                        style={{ color: '#FF9592', fontSize: '15px' }}
                      />
                      <p style={{ fontWeight: 'bold' }}>{value.price}</p>
                    </NavLink>
                  </Col>
                );
              })}
