import React from 'react';
import SwiperCore, {
  Navigation,
  Pagination,
  Scrollbar,
  A11y,
  Autoplay,
} from 'swiper';

import { Swiper, SwiperSlide } from 'swiper/react';
// import useSticky from './../ hooks/useSticky.js'
import useSticky from './../../component/hooks/useSticky.js';
import { backGroundMain, aphorodite } from './data';
import Navbar from './../../component/Navbar/Navbar';
import 'swiper/swiper.scss';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';
import 'swiper/components/scrollbar/scrollbar.scss';
import { Col, Row, Rate } from 'antd';

SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);

const Home = () => {
  const { isSticky, element } = useSticky();
  SwiperCore.use([Autoplay]);
  return (
    <div>
      <Navbar sticky={isSticky} />
      <main style={{ backgroundColor: '#FAFAFA' }}>
        <section className="welcome">
          <div ref={element}>
            <Swiper autoplay={{ delay: 5000 }}>
              {backGroundMain.map((value, index) => {
                return (
                  <SwiperSlide key={index}>
                    <div
                      style={{
                        height: '570px',
                        backgroundImage: `url(${value.image})`,
                        width: '100%',
                        backgroundRepeat: 'no-repeat',
                        backgroundPosition: 'center',
                        backgroundSize: 'cover',
                      }}
                    ></div>
                  </SwiperSlide>
                );
              })}
            </Swiper>
          </div>
        </section>
        <section className="home" style={{ width: '100%' }}>
          <div
            style={{
              marginTop: '150px',
              width: '100%',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <span
              style={{
                fontWeight: 'bold',
                fontSize: '36px',
                marginBottom: '10px',
              }}
            >
              Be Aphrodite Everyday
            </span>
            <span
              style={{
                fontWeight: '300',
                fontSize: '18px',
                fontFamily: 'lato',
              }}
            >
              Show the world the true beauty of a true goddess{' '}
            </span>
          </div>
          <div style={{ margin: '70px' }}>
            <Row>
              {aphorodite.map((value, index) => {
                return (
                  <Col xl={6} lg={12} md={12} sm={12} xs={24}>
                    <div
                      style={{
                        width: '100%',
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                      }}
                    >
                      <div
                        style={{
                          width: '294px',
                          height: '382px',
                          backgroundImage: `url(${value.image})`,
                          backgroundRepeat: 'no-repeat',
                          backgroundPosition: 'center',
                          backgroundSize: 'cover',
                        }}
                      >
                        <div
                          style={{
                            display: 'flex',
                            flexDirection: 'row',
                            padding: '10px 10px',
                            justifyContent: 'space-between',
                          }}
                        >
                          <span
                            style={{
                              backgroundColor: '#fff',
                              fontSize: '14px',
                              fontWeight: '400',
                              color: 'black',
                              padding: '1px 2px',
                            }}
                          >
                            Bestseller
                          </span>
                          <img
                            alt="Vector"
                            src="./image/Home/heart-1.png"
                            style={{ width: '23px', height: '21px' }}
                          />
                        </div>
                      </div>
                      <span
                        style={{
                          fontWeight: 'bold',
                          fontSize: '14px',
                          marginTop: '10px',
                        }}
                      >
                        {value.title}
                      </span>
                      <Rate
                        disabled
                        defaultValue={5}
                        style={{ color: '#FF9592', fontSize: '15px' }}
                      />
                      <p style={{ fontWeight: 'bold' }}>{value.price}</p>
                    </div>
                  </Col>
                );
              })}
            </Row>
            <div
              style={{
                marginTop: '50px',
                width: '100%',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <p
                style={{
                  fontWeight: '700',
                  fontSize: '14px',
                  padding: '12px 43px',
                  backgroundColor: '#565556',
                  color: '#fff',
                }}
              >
                VIEW ALL PRODUCTS
              </p>
            </div>
          </div>
          <div>
            <Row>
              <Col xl={14} lg={14} md={14} sm={24} xs={24}>
                <img src="./image/Home/Group_270.png" style={{width: '822px',height:'858px'}} alt="group 270"/>
              </Col>
              <Col xl={10} lg={10} md={10} sm={24} xs={24}>
                <div style={{padding:'20px'}}>
                  <span style={{wordWrap:'break-word',fontSize:'120px',fontWeight:'700'}}>Classic Beauty</span>
                </div>
              </Col>

            </Row>
          </div>
        </section>
      </main>
    </div>
  );
};

export default Home;
