            </li>
            <li
              className="nav-item active menu-left"
              onMouseEnter={() => setUserIcon('./image/Home/blackPerson.png')}
              onMouseLeave={() => setUserIcon('./image/Home/Vector.png')}
            >
              <NavLink to={token ? '/profile' : '/login'}>
                <img
                  src={token ? './image/Product/productAvt.png' : userIcon}
                  alt="aphro"
                  style={{ width: '23px', height: '23px' }}
                />
              </NavLink>
            </li>
            <li
              className="nav-item active menu-left"
              onClick={() => {
                setIsModaWishList(true);
                setIsModalVisible(false);
                setIsModalLogin(false);
              }}
              style={{ cursor: 'pointer' }}
              onMouseEnter={() => setHeartIcon('./image/Home/blackHeart.png')}
              onMouseLeave={() => setHeartIcon('./image/Home/Vector-2.png')}
            >
              <img
                src={heartIcon}
                alt="heart"
                style={{ width: '23px', height: '23px' }}
              />
            </li>
            <li
              className="nav-item active"
              onClick={() => {
                setIsModalVisible(true);
                setIsModaWishList(false);
                setIsModalLogin(false);
              }}
              style={{ cursor: 'pointer' }}
              onMouseEnter={() => setBagIcon('./image/Home/blackBag.png')}
              onMouseLeave={() => setBagIcon(Vector)}
            >
              <img
                src={bagIcon}
                alt="white"
                style={{ width: '23px', height: '23px' }}
              />
            </li>
          </ul>
        </div>
      </nav>
      <Modal
        title="Shopping Cart"
        visible={isModalVisible || openCard}
        onOk={handleOk}
        onCancel={handleCancel}
        cancelButtonProps={{ style: { display: 'none' } }}
        okButtonProps={{ style: { display: 'none' } }}
      >
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          <div
            style={{
              padding: '32px 35px 60px 35px',
              borderBottom: 'solid 1px #ccc',
            }}
          >
            {modalData.map((value, index) => {
              return (
                <div
                  style={{
                    display: 'flex',
                    flexDirection: 'row',
                    alignItems: 'flex-end',
                    marginBottom: index !== modalData.length - 1 && '35px',
                  }}
                  key={index}
                >
                  <div style={{ position: 'relative' }}>
                    <img
                      src={value.image}
                      style={{
                        width: '140px',
                        height: '170px',
                        marginRight: '30px',
                      }}
                      alt="product"
                    />
                    <div
                      style={{
                        width: '18px',
                        height: '18px',
                        borderRadius: '18px',
                        backgroundColor: 'grey',
                        position: 'absolute',
                        right: '20px',
                        top: '-8px',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                      }}
                    >
                      <span style={{ color: '#cecece' }}>x</span>
                    </div>
                  </div>

                  <div style={{ display: 'flex', flexDirection: 'column' }}>
                    <p
                      style={{
                        fontWeight: '700',
                        fontFamily: 'Lato',
                        fontSize: '14px',
                      }}
                    >
                      {value.title}
                    </p>
                    <p
                      className="fontLato"
                      style={{
                        fontFamily: 'Lato',
                        fontWeight: '300',
                        fontSize: '14px',
                      }}
                    >
                      50ml
                    </p>
                    <p
                      className="fontLato"
                      style={{
                        fontFamily: 'Lato',
                        fontWeight: '300',
                        fontSize: '14px',
                      }}
                    >
                      $ 34
                    </p>
                    <div
                      style={{
                        width: '138px',
                        height: '42px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                        border: 'solid 1px black',
                        paddingLeft: '10px',
                        paddingRight: '10px',
                      }}
                    >
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        -
                      </span>
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        1
                      </span>
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        +
                      </span>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
          <div
            style={{
              display: 'flex',
              flexDirection: 'column',
              padding: '21px 30px 50px 30px',
            }}
          >
            <div
              style={{
                display: 'flex',
                flexDirection: 'row',
                justifyContent: 'space-between',
                width: '100%',
              }}
            >
              <p style={{ fontWeight: '600', fontFamily: 'Lato' }}>Subtotal</p>
              <p style={{ fontWeight: '600', fontFamily: 'Lato' }}>$ 102</p>
            </div>
            <p
              className="fontLato"
              style={{
                fontWeight: '300',
                fontFamily: 'Lato',
                marginBottom: '0px',
              }}
            >
              shipping & taxes will be calculated at check out
            </p>
            <div style={{ marginTop: '30px' }}>
              <NavLink
                to="/shipping-information"
                className="button-main"
                style={{
                  width: '100%',
                  size: 'large',
                  height: '50px',
                  fontFamily: 'Lato',
                }}
              >
                Checkout
              </NavLink>
            </div>
          </div>
        </div>
      </Modal>

      <Modal
        title="Wishlist"
        visible={isModalWishList}
        onOk={handleOk}
        onCancel={handleCancel}
        cancelButtonProps={{ style: { display: 'none' } }}
        okButtonProps={{ style: { display: 'none' } }}
      >
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          <div
            style={{
              padding: '32px 35px 60px 35px',
              borderBottom: 'solid 1px #ccc',
            }}
          >
            {modalData2.map((value, index) => {
              return (
                <div
                  style={{
                    display: 'flex',
                    flexDirection: 'row',
                    marginBottom: index !== modalData.length - 1 && '35px',
                  }}
                  key={index}
                >
                  <div style={{ position: 'relative' }}>
                    <img
                      src={value.image}
                      style={{
                        width: '140px',
                        height: '170px',
                        marginRight: '30px',
                      }}
                      alt="product"
                    />
                    <div
                      style={{
                        width: '18px',
                        height: '18px',
                        borderRadius: '18px',
                        backgroundColor: 'grey',
                        position: 'absolute',
                        right: '20px',
                        top: '-8px',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                      }}
                    >
                      <span style={{ color: '#cecece' }}>x</span>
                    </div>
                  </div>

                  <div style={{ display: 'flex', flexDirection: 'column' }}>
                    <p
                      style={{
                        fontWeight: '700',
                        fontFamily: 'Lato',
                        fontSize: '14px',
                      }}
