                    <p
                      className="fontLato"
                      style={{
                        fontFamily: 'Lato',
                        fontWeight: '300',
                        fontSize: '14px',
                      }}
                    >
                      50ml
                    </p>
                    <p
                      className="fontLato"
                      style={{
                        fontFamily: 'Lato',
                        fontWeight: '300',
                        fontSize: '14px',
                      }}
                    >
                      $ 34
                    </p>
                    <div
                      style={{
                        width: '138px',
                        height: '42px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                        border: 'solid 1px black',
                        paddingLeft: '10px',
                        paddingRight: '10px',
                      }}
                    >
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        -
                      </span>
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        1
                      </span>
                      <span style={{ fontWeight: '700', fontSize: '14px' }}>
                        +
                      </span>
                    </div>
                  </div>
                  <div style={{ marginTop: '5px', marginLeft: '10px' }}>
                    {value.tick ? (
                      <img
                        style={{ width: '18px', height: '18px' }}
                        src="./image/modal/modaltick.png"
                        alt="modal tick"
                      />
                    ) : (
                      <div
                        style={{
                          width: '18px',
                          height: '18px',
                          border: 'solid 1px grey',
                        }}
                      ></div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
          <div
            style={{
              display: 'flex',
              flexDirection: 'column',
              padding: '21px 30px 50px 30px',
            }}
          >
            <p
              className="fontLato"
              style={{
                fontWeight: '300',
                fontFamily: 'Lato',
                marginBottom: '0px',
              }}
            >
              Selected (3) items
            </p>
            <div style={{ marginTop: '30px' }}>
              <Button
                className="button-main"
                style={{
                  width: '100%',
                  fontFamily: 'Lato',
                  size: 'large',
                  height: '50px',
                }}
                onClick={() => {
                  setIsModaWishList(false);
                  setIsModalVisible(true);
                }}
              >
                Add selected items to shopping cart
              </Button>
            </div>
            <div style={{ marginTop: '10px' }}>
              <Button
                className="button-main"
                style={{
                  width: '100%',
                  fontFamily: 'Lato',
                  size: 'large',
                  height: '50px',
                }}
                onClick={() => {
                  setIsModaWishList(false);
                  setIsModalVisible(true);
                }}
              >
                Add all items to shopping cart
              </Button>
            </div>
