import React from 'react';
import SwiperCore, {
  Navigation,
  Pagination,
  Scrollbar,
  A11y,
  Autoplay,
} from 'swiper';

import { Swiper, SwiperSlide } from 'swiper/react';
import useSticky from './../../component/hooks/useSticky.js';
import { backGroundStore,product } from './data';
import Navbar from './../../component/Navbar/Navbar';
import 'swiper/swiper.scss';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';
import 'swiper/components/scrollbar/scrollbar.scss';
import { Col, Row, Rate, Menu, Dropdown } from 'antd';
// import Footer from "./../../component/Footer/Footer"
import Footer from './../../component/Footer/Footer';
import { DownOutlined } from '@ant-design/icons';

SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);

const Store = () => {
  const { isSticky, element } = useSticky();
  SwiperCore.use([Autoplay]);

  const menu = (
    <Menu>
      <Menu.Item key="0">
        <span style={{}}>Categories</span>
      </Menu.Item>
      <Menu.Item key="1">
        <span>Categories 2</span>
      </Menu.Item>
    </Menu>
  );

  return (
    <div>
      <Navbar sticky={isSticky} />
      <main style={{ backgroundColor: '#FAFAFA' }}>
        <section className="welcome">
          <div ref={element}>
            <Swiper autoplay={{ delay: 5000 }}>
              {backGroundStore.map((value, index) => {
                return (
                  <SwiperSlide key={index}>
                    <div
                      style={{
                        height: '570px',
                        backgroundImage: `url(${value.image})`,
                        width: '100%',
                        backgroundRepeat: 'no-repeat',
                        backgroundPosition: 'center',
                        backgroundSize: 'cover',
                      }}
                    ></div>
                  </SwiperSlide>
                );
              })}
            </Swiper>
          </div>
        </section>
        <section
          className="store"
          style={{ width: '100%', padding: '30px 70px' }}
        >
          <Dropdown overlay={menu} trigger={['click']}>
            <div
              style={{
                display: 'flex',
                flexDirection: 'row',
                alignItems: 'center',
                width: '100px'
              }}
            >
              <span
                style={{
                  fontWeight: 'bold',
                //   fontFamily: 'Lato',
                  fontSize: '14px',
                }}
              >
                Categories
              </span>
              <DownOutlined
                style={{
                  color: '#FF9592',
                  fontSize: '14px',
                  marginLeft: '5px',
                }}
              />
            </div>
          </Dropdown>
          <div style={{ margin: '50px' }}>
            <Row>
              {product.map((value, index) => {
                return (
                  <Col xl={6} lg={12} md={12} sm={12} xs={24}>
                    <div
                      style={{
                        width: '100%',
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                      }}
                    >
                      <div
                        style={{
                          width: '294px',
                          height: '382px',
                          backgroundImage: `url(${value.image})`,
                          backgroundRepeat: 'no-repeat',
                          backgroundPosition: 'center',
                          backgroundSize: 'cover',
                        }}
                      >
                        <div
                          style={{
                            display: 'flex',
                            flexDirection: 'row',
                            padding: '10px 10px',
                            justifyContent: 'flex-end',

                          }}
                        >
                          <img
                            alt="Vector"
                            src="./image/Home/heart-1.png"
                            style={{ width: '23px', height: '21px' }}
                          />
                        </div>
                      </div>
                      <span
                        style={{
                          fontWeight: 'bold',
                          fontSize: '14px',
                          marginTop: '10px',
                        }}
                      >
                        {value.title}
                      </span>
                      <Rate
                        disabled
                        defaultValue={5}
                        style={{ color: '#FF9592', fontSize: '15px' }}
                      />
                      <p style={{ fontWeight: 'bold' }}>{value.price}</p>
                    </div>
                  </Col>
                );
              })}
            </Row>
            <div
              style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
            <div
              style={{
                marginTop: '50px',
                width: '236px',
                height:'50px',
                backgroundColor: '#565556',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <span
                style={{
                  fontWeight: '700',
                  fontSize: '14px',
                  color: '#fff',
                }}
              >
                   VIEW DETAILS
              </span>
            </div>

            </div>
          </div>
        </section>
      </main>
    </div>
  );
};

export default Store;
